package com.api.study.riot_api.infrastructure.security.configimport com.api.study.riot_api.global.filter.JwtFilterimport com.api.study.riot_api.infrastructure.security.properties.SecurityPropertiesimport lombok.RequiredArgsConstructorimport org.springframework.context.annotation.Beanimport org.springframework.context.annotation.Configurationimport org.springframework.http.HttpMethodimport org.springframework.security.config.annotation.web.builders.HttpSecurityimport org.springframework.security.config.annotation.web.builders.WebSecurityimport org.springframework.security.config.annotation.web.configuration.EnableWebSecurityimport org.springframework.security.config.annotation.web.configuration.WebSecurityCustomizerimport org.springframework.security.config.http.SessionCreationPolicyimport org.springframework.security.crypto.bcrypt.BCryptPasswordEncoderimport org.springframework.security.web.SecurityFilterChainimport org.springframework.security.web.authentication.UsernamePasswordAuthenticationFilter@Configuration@EnableWebSecurity@RequiredArgsConstructorclass SecurityConfig(    val securityProperties: SecurityProperties) {    @Bean    fun webSecurityCustomizer(): WebSecurityCustomizer {        return WebSecurityCustomizer { web: WebSecurity ->            web.ignoring().antMatchers("/v3/api-docs/**", "/swagger-ui/**", "/swagger-resources/**")        }    }    @Bean    fun securityFilterChain(http: HttpSecurity): SecurityFilterChain {        http            .csrf().disable()            .cors()//            .and()//            .sessionManagement()//            .sessionCreationPolicy(SessionCreationPolicy.STATELESS)            .and()            .authorizeRequests()            //auth            .antMatchers("/api/auth/**").permitAll()            //user//            .antMatchers("/api/user/delete-user/{id}").permitAll()            .anyRequest().authenticated()        return http.build()    }    @Bean    protected fun passwordEncoder() = BCryptPasswordEncoder()}